// @lc code=start
func longestCommonPrefix(strs []string) string {
	return longestCommonPrefix3(strs)
}

// 递归
func longestCommonPrefix3(strs []string) string {
	var lcp func(l, h int) string
	lcp = func(l, h int) string {
		if l >= h-1 {
			return strs[l]
		}

		mid := (h-l+1)/2 + l
		lcpleft, lcpright := lcp(l, mid), lcp(mid, h)
		i, j := 0, 0
		for i < len(lcpleft) && j < len(lcpright) {
			if lcpleft[i] != lcpright[j] {
				return lcpleft[:i]
			}
			i++
			j++
		}
		return lcpleft[:i]
	}

	return lcp(0, len(strs))
}

// 二分
func longestCommonPrefix2(strs []string) string {
	mark := strs[0]
	minl := len(mark)
	for _, s := range strs {
		if len(s) < minl {
			minl = len(s)
		}
	}

	isPrefix := func(l int) bool {
		for i := 1; i < len(strs); i++ {
			if strs[i][:l] != mark[:l] {
				return false
			}
		}
		return true
	}

	low, hight := 0, minl
	for low < hight {
		mid := (hight-low+1)/2 + low
		if isPrefix(mid) {
			low = mid
		} else {
			hight = mid - 1
		}
	}
	return mark[:low]
}

// 列扫描比较
func longestCommonPrefix1(strs []string) string {
	j := 0
IDX:
	for {
		var tmp byte
		for i := 0; i < len(strs); i++ {
			if j >= len(strs[i]) || (tmp != 0 && strs[i][j] != tmp) {
				break IDX
			}
			tmp = strs[i][j]
		}
		j++
	}
	return strs[0][:j]
}

